import { createStore } from 'redux';
import { combineReducers } from 'redux';


function walletReducer(state = JSON.parse(localStorage.getItem('wallets')), action) {
    switch (action.type) {
        case 'ADD_WALLET':
            state.wallets.push(action.payload);
            localStorage.setItem('wallets', JSON.stringify(state));
            state.wallets.pop(action.payload);
            return {
                wallets: [...state.wallets, action.payload]
            };
        case 'DELETE_WALLET':
            debugger
            state.wallets.filter(wlt => wlt !== action.payload.wallet_name);
            localStorage.setItem('wallets', JSON.stringify(state));
            state.wallets.push(action.payload);
            debugger
            return {
                ...state,
                wallets: state.wallets.filter(wallet => wallet !== action.payload.wallet_name)
            };    
        default:
            return state;
    }
}

const rootReducer = combineReducers({
    wallets: walletReducer,
})

const store = createStore(rootReducer);

export default store;
