import { createStore } from 'redux';
import { combineReducers } from 'redux';


function walletReducer(state = JSON.parse(localStorage.getItem('wallets')), action) {
    switch (action.type) {
        case 'ADD_WALLET':
            state = JSON.parse(localStorage.getItem('wallets'))
            state.wallets.push(action.payload);
            localStorage.setItem('wallets', JSON.stringify(state));
            state.wallets.pop(action.payload);
            return {
                wallets: [...state.wallets, action.payload]
            };
        case 'DELETE_WALLET':
            var indexToInsert = 0;
            var updatedArray = []
            state.wallets.forEach((wlt, index) => {
                if (wlt.wallet_name === action.payload.wallet_name) {
                    updatedArray = state.wallets.splice(index, 1);
                    indexToInsert = index;
                }
            })
            localStorage.setItem('wallets', JSON.stringify(updatedArray));
            //state.wallets.splice(indexToInsert, 0, action.payload);
            return {
                ...state,
                wallets: [...state.wallets, updatedArray]
            };    
        default:
            return state;
    }
}

const rootReducer = combineReducers({
    wallets: walletReducer,
})

const store = createStore(rootReducer);

export default store;
